parameterTemplate:
{
    /*
     * The title attribute is a string with a text to show in the top area of
     * the screen.
     */
    "title": "Item list",

    /*
     * The logo attribute is a string containing the url of the logo to show in
     * the top right area of the screen
     */
    "logo":  "http://demo.fast.morfeo-project.org/fast/images/gui/fastLogo.png",

    /*
     * The columns attribute is a list of column descriptions, each of them containing
     * the following attributes:
     *    - field  (required): the input data field to be shown in the column
     *    - title  (required): the label to show in the header of the column
     *    - type   (required): data type. It can be text or an image url (img)
     *    - width  (optional): cell width
     *    - height (optional): cell height
     * 
     * If this attribute is not defined, the following columns will be used
     */
    "columns": [
            {
                "field": "image",
                "title": "",
                "type":  "img",
                "width": "80px",
                "heigth": "80px"
            },{
                "field": "source",
                "title": "Source",
                "type":  "text",
                "width": "10%"
            },{
                "field": "title",
                "title": "Title",
                "type":  "text",
                "width": "50%"
            }, {
                "field": "type",
                "title": "Product Group",
                "width": "20%"
            }, {
                "field": "price",
                "title": "Price",
                "type":  "text"
            }
    ]
}
